  const article = item.json;
  return {
    json: {
      title: article.title,
      url: article.url,
      source: 'Dev.to',
      author: article.user?.name || 'Unknown',
      score: (article.public_reactions_count || 0) + (article.comments_count || 0) * 2,
      reactions: article.public_reactions_count || 0,
      comments: article.comments_count || 0,
      published_at: article.published_at
    }
  };
});

const response = $input.first().json;
const posts = response.data?.children || [];
return posts.slice(0, 5).map(item => {
  const post = item.data;
  return {
    json: {
      title: post.title,
      url: post.url,
      source: 'Reddit',
      author: post.author,
      score: (post.score || 0) + (post.num_comments || 0),
      reactions: post.score || 0,
      comments: post.num_comments || 0,
      published_at: new Date(post.created_utc * 1000).toISOString()
    }
  };
});

const allItems = $input.all();
const topIds = allItems.slice(0, 5);
return topIds.map(item => ({json: { storyId: item.json }}));

const items = $input.all();
return items
  .filter(item => item.json.type === 'story' && item.json.url)
  .map(item => {
    const story = item.json;
    return {
      json: {
        title: story.title,
        url: story.url,
        source: 'Hacker News',
        author: story.by || 'Unknown',
        score: (story.score || 0) + (story.descendants || 0),
        reactions: story.score || 0,
        comments: story.descendants || 0,
        published_at: new Date(story.time * 1000).toISOString()
      }
    };
  });

const xml2js = require('xml2js');
const parser = new xml2js.Parser();
const rss = $input.first().json;

let items = [];
try {
  const result = await parser.parseStringPromise(rss);
  const feedItems = result.rss?.channel?.[0]?.item || [];
  items = feedItems.slice(0, 5).map(item => {
    return {
      json: {
        title: item.title?.[0] || 'No title',
        url: item.link?.[0] || '',
        source: 'Medium',
        author: item['dc:creator']?.[0] || 'Unknown',
        score: 0,
        reactions: 0,
        comments: 0,
        published_at: item.pubDate?.[0] || new Date().toISOString()
      }
    };
  });
} catch (e) {
  items = [{ json: { error: 'Failed to parse Medium RSS' } }];
}

return items;

