const items = $input.all();
const half = Math.floor(items.length / 2);
const articles = items.slice(0, half);
const summaries = items.slice(half);
const source = articles[0]?.json?.source || 'Unknown';

function escapeMarkdown(text) {
  if (!text) return '';
  return text.replace(/[_*\[\]()~`>#+=|{}.!-]/g, '\\$&');
}

const combined = articles.map((item, index) => {
  const article = item.json;
  let summary = '';
  if (summaries[index]) {
    const sumData = summaries[index].json;
    if (sumData.message) {
      summary = sumData.message.content;
    }
  }
  return { ...article, summary };
});

